import React, { useState } from 'react';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({ email: '', password: '' });
  const [apiResponse, setApiResponse] = useState('');

  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*]).{8,}$/;
    return regex.test(password);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let valid = true;
    let emailError = '';
    let passwordError = '';

    if (!validateEmail(email)) {
      emailError = 'Invalid email format';
      valid = false;
    }

    if (!validatePassword(password)) {
      passwordError = 'Password must be at least 8 characters long and include an uppercase letter, a number, and a symbol';
      valid = false;
    }

    if (valid) {
      // Submit the form data to the API
      try {
        const response = await fetch('https://dms-api.apps.ginnsltd.com/v1/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ email, password })
        });

        const data = await response.json();
        console.log('API Response:', data);

        if (data.ok) {
          setApiResponse('Login successful');
        } else {
          setApiResponse(data.message);
        }
      } catch (error) {
        console.error('Error:', error);
        setApiResponse('An error occurred. Please try again.');
      }
    } else {
      setErrors({ email: emailError, password: passwordError });
    }
  };

  return (
    <div className='w-full h-screen flex justify-center items-center'>
      <div className='w-full max-w-md flex flex-col justify-center items-center'>
        <div className='flex justify-center'>
          <h1 className='font-extrabold'>BLESSING</h1>
        </div>
        <div className="w-full flex justify-center items-center mt-6">
          <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 w-full">
            <h2 className="mb-4 text-xl font-bold text-center">Login</h2>
            <div className="mb-4 mt-10">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
                Email
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="email"
                type="text"
                name="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              {errors.email && <p className="text-red-500 text-xs italic">{errors.email}</p>}
            </div>
            <div className="mb-6">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
                Password
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
                id="password"
                type="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              {errors.password && <p className="text-red-500 text-xs italic">{errors.password}</p>}
            </div>
            <div className="flex justify-center">
              <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit">
                Login
              </button>
            </div>
            {apiResponse && <p className="text-red-500 text-xs italic text-center mt-4">{apiResponse}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}
